sql.where:
DEV.DELETED = 0

	AND DEV.STATE = #{para.state}


	AND TSP.NAME LIKE '%${para.keyword}%'

mapper.select:
SELECT 
	TT.ID,TT.STATE,TT.SYNC_STATE,TT.SIGN_POINT_NAME,TT.DEVICE_NAME,TT.SN,TT.TOTAL_PEOPLE,TT.UNSYNC_PEOPLE
FROM(
	SELECT R.*, ROWNUM RN
	FROM (

	) R
	where ROWNUM < (#{para.startIndex} + #{para.pageSize})
) TT WHERE TT.RN >= #{para.startIndex}

mapper.update:
UPDATE T_DEVICE
	SET DELETED=1
WHERE ID=#{id}
