mapper.select:
SELECT TT.ID,TT.STATE,TT.NAME, DEPT.ID JOB_DEPT_ID,DEPT.NAME JOB_DEPT_NAME,JOB.ID JOB_ID,JOB.NAME JOB_NAME, EMP.ID EMPLOYEE_ID,EMP.NAME EMPLOYEE_NAME, TSP.ID SIGN_POINT_ID,TSP.NAME SIGN_POINT_NAME, DEPT2.ID PLACE_DEPT_ID,DEPT2.NAME PLACE_DEPT_NAME FROM( SELECT R.*, ROWNUM RN FROM (
) R where ROWNUM < (#{para.startIndex} + #{para.pageSize}) ) TT
LEFT JOIN T_SIGN_GROUP_DEPT TSGD ON TSGD.SIGN_GROUP_ID = TT.ID AND TSGD.DELETED = 0 LEFT JOIN T_DEPT DEPT ON DEPT.ID = TSGD.DEPT_ID AND DEPT.DELETED = 0 AND DEPT.STATE = 1 LEFT JOIN T_JOB JOB ON JOB.ID = TSGD.JOB_ID AND JOB.DELETED = 0 AND JOB.STATE = 1 LEFT JOIN T_EMPLOYEE EMP ON EMP.ID = TSGD.EMPLOYEE_ID AND EMP.DELETED = 0 AND EMP.JOB_STATE !=3
LEFT JOIN T_SIGN_GROUP_SIGN_POINT TSGSP ON TSGSP.SIGN_GROUP_ID = TT.ID AND TSGSP.DELETED = 0 LEFT JOIN T_SIGN_POINT TSP ON TSP.ID = TSGSP.SIGN_POINT_ID AND TSP.DELETED = 0 LEFT JOIN T_DEPT DEPT2 ON DEPT2.ID = TSGSP.DEPT_ID AND DEPT2.DELETED = 0 AND DEPT2.STATE = 1
WHERE TT.RN >= #{para.startIndex}
ORDER BY TT.CREATED_DATE DESC

mapper.update:
UPDATE 
	T_SIGN_GROUP 
SET STATE = #{state} 
WHERE ID = #{id}
