mapper.select:
select 
	*
from 
	yd_tender
where 
	tender_tid = #{taskId}

		 and tender_utype > 0
		 and tender_utype = #{type}

	limit #{startPageNum},#{queryPageNum}

mapper.update:
update yd_tender
tender_price = #{tender_price}, tender_pnum = #{tender_pnum}, tender_conts = #{tender_conts}, tender_update_time = #{tender_update_time},
where tender_id = #{tender_id} and tender_tid = #{tender_tid} and tender_uid = #{tender_uid}

update yd_tender
set 
	tender_type = #{type},
	tender_state = #{state},
	tender_update_time=#{updateTime}
where tender_id = #{tenderId}

update yd_tender
set tender_state = #{tender_state},tender_update_time=#{tender_update_time}
where tender_id = #{yd_tender}
