mapper.insert:
insert into yd_epcs
	(epcs_tid,epcs_tname,epcs_eid,epcs_ename,epcs_term,epcs_contents,epcs_evaluation,epcs_reason,epcs_create_time,epcs_update_time)
select 
	#{epcs_tid},task_pname,#{epcs_eid},#{epcs_ename},task_term,#{epcs_contents},#{epcs_evaluation},#{epcs_reason},#{epcs_create_time},#{epcs_update_time}
from yd_task where task_id = #{epcs_tid}

mapper.update:
update yd_epcs
epcs_tid=#{epcs_tid}, epcs_money=#{epcs_money}, epcs_pmoney=#{epcs_pmoney}, epcs_sid=#{epcs_sid}, epcs_duration=#{epcs_duration}, epcs_level=#{epcs_level}, epcs_tid=#{epcs_desc}, epcs_contents=#{epcs_contents}, epcs_create_time=#{epcs_create_time}, epcs_update_time=#{epcs_update_time},
where epcs_id = #{epcs_id}
