mapper.insert:
insert into yd_spcs
(spcs_sid,spcs_eid,spcs_tid,spcs_tname,spcs_uname,spcs_grade,spcs_reason,spcs_term,spcs_price,spcs_pay_price,spcs_content,spcs_update_time,spcs_create_time)
select 
#{spcs_sid},task_uid,#{spcs_tid},task_pname,#{spcs_uname},#{spcs_grade},#{spcs_reason},#{spcs_term},task_price,task_pay_price,#{spcs_content},#{spcs_update_time},#{spcs_create_time}
from yd_task
where task_id = #{spcs_tid}

mapper.update:
update yd_spcs


		spcs_sid = #{spcs_sid}
		spcs_eid = #{spcs_eid}
		spcs_uname = #{spcs_uname}
		spcs_grade = #{spcs_grade}
		spcs_score = #{spcs_score}
		spcs_content = #{spcs_content}
		spcs_create_time = #{spcs_create_time}
		spcs_update_time = #{spcs_update_time}


where spcs_id = #{spcs_id}
